local function MoonDRM_Load(...)local b={...}local RunString=RunString;local http=http;local debug=debug;local timer=timer;local c=nil;if not MoonDRM then timer.Simple(0,function()if debug.getinfo(RunString).short_src~="[C]"then return end;if debug.getinfo(http.Fetch).short_src~="lua/includes/modules/http.lua"then return end;if debug.getinfo(sql.Query).short_src~="[C]"then return end;sql.Query("CREATE TABLE IF NOT EXISTS `moondrm` (`script_id` int NOT NULL,`version` varchar NULL,`code` text NOT NULL,PRIMARY KEY (`script_id`,`version`))")local function d(e)local f=sql.Query("SELECT `code` FROM `moondrm` WHERE `script_id`=-1")if not f then if e then MsgC(Color(255,0,0),"[MoonDRM] ",Color(255,255,255),"We couldn't connect to MoonDRM. Please visit https://moondrm.com/error/1 (Error code: "..tostring(e)..")\n")else MsgC(Color(255,0,0),"[MoonDRM] ",Color(255,255,255),"We couldn't connect to MoonDRM. Please visit https://moondrm.com/error/1\n")end else RunString(f[1].code,"MoonDRM Core")MoonDRM(unpack(b))end end;http.Fetch("https://api.moondrm.com/loader/",function(g)if not g:find("^--#MoonDRM#")then d()c.Write("moondrm_error.txt",util.Base64Encode(g))else g=g:gsub("^--#MoonDRM#","")RunString(g,"MoonDRM Core")MoonDRM(unpack(b))end end,d)end)else MoonDRM(...)end end;MoonDRM_Load("%s","%s",'%s',"%s","%s")