if MoonDRM then return end;function MoonDRM(a,b,c,d,e)if not a then error("You need to provide a ScriptID. https://panel.moondrm.com/documentation/")end;if not e then error("You need to provide the script's name. https://panel.moondrm.com/documentation/")end;if not c then error("You need to provide a hash. https://panel.moondrm.com/documentation/")end;if not b then error("You need to provide Version. https://panel.moondrm.com/documentation/")end;if not d then error("You need to provide SteamID64. https://panel.moondrm.com/documentation/")end;if _G.debug.getinfo(_G.RunString).short_src=="[C]"then local f={}if system.IsWindows()then f["OS"]=0 elseif system.IsLinux()then f["OS"]=1 elseif system.IsOSX()then f["OS"]=2 end;f["ScriptID"]=a;f["Version"]=b;f["ID"]=c;f["IP"]=game.GetIPAddress():gsub(":.*","")f["Port"]=game.GetIPAddress():gsub(".-:","")f["Hostname"]=GetConVar("hostname"):GetString()f["SteamID64"]=d;for g,h in pairs(f)do f[g]=tostring(h)end;http.Post("https://api.moondrm.com/",f,function(i)if i:find("^--#MoonDRM#\n")then i=i:gsub("^--#MoonDRM#\n","")sql.Query("REPLACE INTO `moondrm` (`script_id`,`version`,`code`) VALUES("..sql.SQLStr(a)..","..sql.SQLStr(b)..","..sql.SQLStr(i)..")")if _G.debug.getinfo(_G.RunString).short_src=="[C]"then _G.RunString(i,e)elseif c then http.Post("https://panel.moondrm.com/detour.php",{ID=c})end elseif i:find("^--#MoonDRM%-Error#\n")then RunString(i:gsub("^--#MoonDRM%-Error#\n",""),"MoonDRM Backend")else file.Write("moondrm_error_c.txt",util.Base64Encode(i))end end)elseif c then http.Post("https://panel.moondrm.com/detour.php",{ID=c})end end